/*QuestClose.cA high-level user-callable routine to free all the arrays allocated byQuestOpen()HISTORY:6/9/90	dgp	 wrote it.*/#include "Quest.h"#include <stdlib.h>void QuestClose(Quest *q){	int cond,r;		/* Free up memory taken up by dynamically allocated conditions.*/	for(r=0; r<RESPONSES; r++){		free(q->qConds[0]->pResponse[r]);		q->qConds[0]->pResponse[r]=NULL;	}	free(q->qConds[0]->pInitial);	q->qConds[0]->pInitial=NULL;	for(cond=0; cond<q->nConds; cond++)		q_freeCond(&q->qConds[cond]);}